import React, { useEffect, useState } from "react";
import { Card } from 'primereact/card';
import axios from 'axios';
import { Dropdown } from 'primereact/dropdown';

export default function BasicDemo() {
  const baseURL = "https://jsonplaceholder.typicode.com/users";
  const [userData, setuserDataOptions] = useState([]);
  
  useEffect(() => {
    axios
      .get(`${baseURL}`)
      .then((res) => {
        setuserDataOptions(res.data);
      })
      .catch((err) => {
        console.log("error here")
        console.log(err);
      });
    },[]);
    const [selectedUser, setSelectedUser] = useState(userData[0]);
    const users = userData.map(user => user.name);
   //console.log(selectedUser);
   
    console.log("users : ",users);
  //   return (
  //       <div className="card">
  //         <div className="card flex justify-content-center">
  //         <Dropdown value={selectedUser} onChange={(e) => setSelectedUser(e.value)} options={users} optionLabel="name" 
  // editable placeholder="Select a User" className="w-full md:w-14rem" />

  //      </div>
  //           <Card title="{users}">
  //               <p className="m-0">
  
  //               </p>
  //           </Card>
  //       </div>
  //   )
  return (
    <div className="card">
      {userData.length > 0 && ( // Render the Dropdown component only when userData has data
        <div className="card flex justify-content-center">
          <Dropdown
            value={selectedUser}
            onChange={(e) => setSelectedUser(e.value)}
            options={userData}
            optionLabel="name"
            editable
            placeholder="Select a User"
            className="w-full md:w-14rem"
          />
        </div>
      )}
      {selectedUser && (<Card title={selectedUser.name}>
      
        <p className="m-0">UserName:{selectedUser.username}</p>
        <p className="m-0">{selectedUser.email}</p>
        <p className="m-0">{ JSON.stringify(selectedUser.address)}</p>
        <p className="m-0">{selectedUser.phone}</p>
        <p className="m-0">{selectedUser.website}</p>
        <p className="m-0">{ JSON.stringify(selectedUser.company)}</p>
        {/* <p className="m-0">{selectedUser.}</p> */}
        
      </Card>)}
    </div>
  );
  
}





