import React, { useEffect, useState } from "react";
import { Card } from 'primereact/card';
import axios from 'axios';
import { Dropdown } from 'primereact/dropdown';

export default function BasicDemo() {
  const baseURL = "https://jsonplaceholder.typicode.com";
  const [userData, setuserDataOptions] = useState([]);
  
  useEffect(() => {
    axios
      .get(`${baseURL}/users`)
      .then((res) => {
        setuserDataOptions(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
    },[]);
    const [selectedUser, setSelectedUser] = useState(null);
    const users = userData.map(user => user.name);
   //console.log(userData);
    console.log("nam",users);
    return (
        <div className="card">
          <div className="card flex justify-content-center">
              <Dropdown value={setSelectedUser} onChange={(e) => setSelectedUser(e.value)} options={selectedUser} optionLabel="name" 
               editable placeholder="Select a City" className="w-full md:w-14rem" />
       </div>
            <Card title="{users}">
                <p className="m-0">
  
                </p>
            </Card>
        </div>
    )
}





